mapper.select:
select *, IFNULL((select state from oj_blog_user_like where oj_blog_user_like.uid = #{param3} and bid = oj_blog_list.id) ,0) as like_state, IFNULL((select state from oj_blog_user_star where oj_blog_user_star.uid = #{param3} and bid = oj_blog_list.id) ,0) as star_state from oj_blog_list left join oj_user on oj_user.id = oj_blog_list.uid
where type like #{param4}
order by oj_blog_list.time desc limit #{param1},#{param2}
